import shopActionType from './shopTypes';

const INITIAL_STATE = {
    collections: SHOP_DATA
}


const shopReducer = (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case shopActionType.UPDATE_COLLECTIONS:
        return {
            ...state,
            collections: action.payload
        }
        default:
            return state;
    }
}


export default shopReducer
